INSERT INTO `pages` VALUES (2, 1, 2, 'Contact Information', 'Resume Contact Section Example\r\nFirst Name Last Name\r\nYour Address\r\nYour City, State Zip Code\r\nYour Phone Number\r\nYour Email Address\r\nLinkedIn or Personal Website URL (if you have one)\r\n\r\nEdit, Edit, Edit\r\nBe sure to proofread your contact information, along with the rest of the information on your resume. A typo in your phone number or email address is more common than you might think. It could prevent you from getting contacted for a job interview.', NULL, NULL, '2018-12-03 11:31:17', NULL, NULL);
INSERT INTO `pages` VALUES (3, 1, 3, 'Контакты', 'Контакты Windows (англ. Windows Contacts) — это менеджер контактов, который включён в состав Windows Vista, \r\nWindows 7 и Windows 8. Он пришёл на смену адресной книги Windows, но сохранил большую часть функциональности \r\nи работает с Windows Mail.\r\n\r\nВ Windows Contacts используется новый формат на основе схемы — XML. Для хранения пользовательской информации, \r\nконтактов и фотографий используется файл \r\nindividual.contact. Windows Contacts имеет интерфейс программирования приложений (API) для интеграции с другими \r\nприложениями и для хранения пользовательской информации. Windows Contacts умеет работать с форматами \r\n.wab и стандартным форматом .vcf (визитная карточка), также поддерживается формат .csv.', NULL, NULL, '2018-12-03 11:32:18', NULL, NULL);
